mapper.update:
UPDATE lecture SET categoryCode= #{categoryCode}, lectureSubject = #{lectureSubject}, 
	lectureContent = #{lectureContent}, lecturePrice = #{lecturePrice},
	dcPercent = #{dcPercent}, lectureDuration = #{lectureDuration}, thumbNail = {thumbNail}
WHERE memberEmail = #{memberEmail}

mapper.select:
SELECT * FROM(
    SELECT ROWNUM rnum, tb.* FROM(
        select l.lectureNum, l.memberEmail instMail, m.membername instName , lectureSubject subject, lecturePrice price, l.regDate, dcpercent , 
            lectureDuration duration, categoryCode category, lectureContent content, lecturestate state
        FROM lecture l
        JOIN member m on l.memberEmail = m.memberEmail


				lectureState = #{state}


    )tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
