mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT inquiryNum, qMember, question, TO_CHAR(q_regDate, 'YYYY-MM-DD') q_regDate, aMember, answer, a_regDate
		FROM lectureInquiry l
		WHERE lectureNum = #{lectureNum}
		ORDER BY inquiryNum DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
